function getColour(){return choose(1+5*Math.random(),["red","green","gold","blue","orange"])}function choose(o,e){return e[Math.round(o)-1]}for(const o of document.getElementsByTagName("table")){o.style.tableLayout="fixed",o.style.border="1px solid "+getColour();for(const e of o.rows)for(const o of e.cells)o.style.border="1px solid "+getColour()}